// --- MEDIA TOOL (VIDEO/AUDIO) ---
class LiveMediaTool {
    static get toolbox() {
        return {
            title: 'Video/Audio',
            icon: 'ðŸŽ¥'
        };
    }
    data;
    wrapper;
    descriptionField = null;
    constructor({ data }){
        this.data = data || {
            url: '',
            type: '',
            name: '',
            description: ''
        };
        this.wrapper = document.createElement('div');
    }
    render() {
        this.wrapper.style.padding = "15px";
        this.wrapper.style.border = "1px solid #f0f0f0";
        this.wrapper.style.borderRadius = "10px";
        this.wrapper.style.background = "#fafafa";
        if (this.data && this.data.url) {
            this._renderPlayer(this.data.url, this.data.type);
        } else {
            const input = document.createElement('input');
            input.type = 'file';
            input.accept = 'video/*,audio/*';
            input.style.display = 'none';
            input.onchange = (e)=>{
                const file = e.target.files[0];
                if (!file) return;
                const reader = new FileReader();
                reader.onload = (event)=>{
                    const url = event.target?.result;
                    const type = file.type.startsWith('video') ? 'video' : 'audio';
                    this.data = {
                        ...this.data,
                        url,
                        type,
                        name: file.name
                    };
                    this._renderPlayer(url, type);
                };
                reader.readAsDataURL(file);
            };
            this.wrapper.appendChild(input);
            setTimeout(()=>input.click(), 0);
        }
        return this.wrapper;
    }
    _renderPlayer(url, type) {
        this.wrapper.innerHTML = '';
        const mediaTag = document.createElement(type);
        mediaTag.src = url;
        mediaTag.controls = true;
        mediaTag.style.width = "100%";
        const desc = document.createElement('div');
        desc.contentEditable = "true";
        desc.dataset.placeholder = "Add a description...";
        desc.innerHTML = this.data.description || '';
        desc.style.cssText = "margin-top:10px; padding:8px; border-left:2px solid #0070f3; background: #fff;";
        desc.addEventListener('input', ()=>{
            this.data.description = desc.innerHTML;
        });
        this.descriptionField = desc;
        this.wrapper.appendChild(mediaTag);
        this.wrapper.appendChild(desc);
    }
    save() {
        return {
            ...this.data,
            description: this.descriptionField ? this.descriptionField.innerHTML : this.data.description
        };
    }
}
// --- CONVERTER ---
const convertToMarkdown = (data)=>{
    let markdown = "";
    data.blocks.forEach((block)=>{
        // Handle Custom H1-H6
        if (block.type.match(/^h[1-6]$/)) {
            const level = block.type.charAt(1);
            markdown += `${"#".repeat(parseInt(level))} ${block.data.text}\n\n`;
        } else {
            switch(block.type){
                case "header":
                    markdown += `${"#".repeat(block.data.level || 2)} ${block.data.text}\n\n`;
                    break;
                case "paragraph":
                    markdown += `${block.data.text}\n\n`;
                    break;
                case "quote":
                    markdown += `> ${block.data.text}\n`;
                    if (block.data.caption) markdown += `> â€” *${block.data.caption}*\n`;
                    markdown += `\n`;
                    break;
                case "code":
                    markdown += `\`\`\`\n${block.data.code}\n\`\`\`\n\n`;
                    break;
                case "media":
                    const tag = block.data.type === 'video' ? 'video' : 'audio';
                    markdown += `<${tag} controls src="${block.data.url}" style="width:100%"></${tag}>\n`;
                    if (block.data.description) markdown += `\n*${block.data.description.replace(/<[^>]*>/g, '')}*\n\n`;
                    else markdown += `\n`;
                    break;
                case "embed":
                    markdown += `<iframe width="100%" height="400" src="${block.data.embed}" frameborder="0" allowfullscreen></iframe>\n`;
                    if (block.data.caption) markdown += `\n*${block.data.caption}*\n\n`;
                    break;
                case "delimiter":
                    markdown += `---\n\n`;
                    break;
                case "list":
                    block.data.items.forEach((item, i)=>{
                        markdown += block.data.style === "ordered" ? `${i + 1}. ${item}\n` : `- ${item}\n`;
                    });
                    markdown += "\n";
                    break;
                case "image":
                    markdown += `![${block.data.caption || ""}](${block.data.file.url})\n\n`;
                    break;
                default:
                    if (block.data.text) markdown += `${block.data.text}\n\n`;
                    break;
            }
        }
    });
    return markdown.trim();
};
